#设置字符集
SET NAMES UTF8;
#丢弃数据库tar如果有的话
DROP DATABASE IF EXISTS tar;
#创建数据库tar,并设置字符集
CREATE DATABASE tar CHARSET=UTF8;
#进入数据库tar;
USE tar;

###### 创建表格 start ######

#创建用户表
CREATE TABLE tar_user(
	uid INT PRIMARY KEY AUTO_INCREMENT,
	uname VARCHAR(10),	                #用户名
	upwd VARCHAR(8),                    #登录密码
	uemail VARCHAR(64),                 #用户邮箱
	phone VARCHAR(16) NOT NULL UNIQUE,  #手机号码
	gender INT		                      #性别 1-男 2-女
);

#创建管理员账号tar_root
CREATE TABLE tar_root(
	rid INT PRIMARY KEY AUTO_INCREMENT,
	root_name VARCHAR(10),	#管理员名称
	root_pwd VARCHAR(12)	  #管理员密码
);

#创建产品表
CREATE TABLE tar_product(
	pid INT PRIMARY KEY AUTO_INCREMENT,
	sid INT,	                #产品编号
	title VARCHAR(64),        #产品标题
	price INT,                #产品价格
	origin VARCHAR(64),       #出发地
	destination VARCHAR(256), #目的地
	theme VARCHAR(64),        #主题
	days INT,                 #天数
	outlines VARCHAR(256),    #产品概要
	team_name VARCHAR(1024),   #团队名称
	feature VARCHAR(2048),    #产品特色
	introduce VARCHAR(2048)   #产品介绍
);

#创建产品图片表
CREATE TABLE tar_product_img(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  sid INT, #产品编号
  sm VARCHAR(128),  #小图地址
  md VARCHAR(128),  #中图地址
  lg VARCHAR(128)   #大图地址
);

#创建首页轮播图表
CREATE TABLE tar_index_carousel(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  img VARCHAR(64),     #轮播图片
  title VARCHAR(32),   #轮播图片信息
  href VARCHAR(64)     #跳转地址
);

#创建首页前十榜表
CREATE TABLE tar_index_top(
  tid INT PRIMARY KEY AUTO_INCREMENT,
  country VARCHAR(32),      #国家
  place VARCHAR(32),        #地区
  area VARCHAR(32),         #关键词-区域
  ranking INT,              #关键词-排名
  img VARCHAR(64)           #前十榜图片
);

#创建人气指南详情表
CREATE TABLE tar_guide(
  gid INT PRIMARY KEY AUTO_INCREMENT,
  sm VARCHAR(128),              #指南小图
  md VARCHAR(128),              #指南大图
  kw VARCHAR(10),               #关键词-大类
  title VARCHAR(64),            #指南标题
  update_date VARCHAR(64),      #更新日期
  file_size VARCHAR(10),        #文件大小
  outlines VARCHAR(1024),       #指南介绍
  hotel VARCHAR(64),            #酒店名称
  hotel_intr VARCHAR(1024),     #酒店介绍
  hotel_pic VARCHAR(128),       #酒店图片
  scenic VARCHAR(64),           #景点名称
  scenic_intr VARCHAR(1024),    #景点介绍
  scenic_pic VARCHAR(128),      #景点图片
  dining VARCHAR(64),           #餐馆名称
  dining_intr VARCHAR(1024),    #餐馆介绍
  dining_pic VARCHAR(128),      #餐馆图片
  shopping VARCHAR(64),         #购物名称
  shopping_intr VARCHAR(1024),  #购物介绍
  shopping_pic VARCHAR(128),    #购物图片
  banners VARCHAR(128)          #轮播图片
);

#旅游游记详情表
CREATE TABLE tar_notes(
  nid INT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(128),         #游记标题
  publish_date VARCHAR(32),   #发布日期
  writer VARCHAR(32),         #作者
  go_off VARCHAR(32),         #出发日期
  dest_name VARCHAR(64),      #目的地
  days INT(6),                #出行天数
  text_brief VARCHAR(128),    #游记概要
  text_part VARCHAR(2048),    #游记正文
  header_pic VARCHAR(128),    #游记头部图片
  content_pic VARCHAR(128)    #游记主体图片
);

#行程订单表
CREATE TABLE tar_shoppingcart_item(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  uid INT,                    #用户id
  pid INT,                    #产品id
  count INT,                  #出行人数
  is_checked INT,             #是否选中 0-未选中 1-已选中
  status INT                  #订单状态 0-未提交 1-已提交 2-已付款 3-已出行
);

#用户收藏表
CREATE TABLE tar_user_collect(
  cid INT PRIMARY KEY AUTO_INCREMENT,
  uid INT,                    #用户id
  pid INT,                    #产品id
  is_collect INT              #是否收藏 0-未收藏 1-已收藏
);

###### 创建表格 end ######c

